// src/App.js  (or App.jsx)
import { useState } from "react";

export default function App() {
  const [url, setUrl] = useState("");
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleScrape = async () => {
    if (!url) return;
    setLoading(true);
    setError("");
    setArticles([]);

    try {
      const res = await fetch(
        `http://localhost:5000/scrape?url=${encodeURIComponent(url)}`
      );
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || "Scrape failed");
      setArticles(Array.isArray(data) ? data : []);
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Header */}
      <header className="p-6 text-center bg-gradient-to-r from-blue-600 to-purple-600 shadow-lg">
        <h1 className="text-4xl font-extrabold tracking-wide">üì∞ News Scraper</h1>
        <p className="text-gray-200 mt-2">Cheerio-powered backend ‚Ä¢ React frontend</p>
      </header>

      {/* Input */}
      <div className="flex justify-center mt-8">
        <div className="bg-white/10 backdrop-blur-lg p-6 rounded-2xl shadow-xl w-full max-w-2xl">
          <div className="flex gap-3">
            <input
              type="text"
              placeholder="https://news.ycombinator.com"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              className="flex-grow p-3 rounded-lg text-black focus:ring-2 focus:ring-blue-500"
            />
            <button
              onClick={handleScrape}
              className="bg-blue-600 hover:bg-blue-700 px-6 py-3 rounded-lg font-semibold transition"
            >
              Scrape
            </button>
          </div>
          {loading && <p className="mt-3 text-sm text-blue-200">‚è≥ Scraping‚Ä¶</p>}
          {error && <p className="mt-3 text-sm text-red-300">‚ö†Ô∏è {error}</p>}
        </div>
      </div>

      {/* Articles */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8 p-8">
        {articles.map((a, i) => (
          <div
            key={i}
            className="bg-gray-800/80 rounded-2xl shadow-lg overflow-hidden hover:scale-105 transform transition duration-300"
          >
            <div className="h-40 bg-gradient-to-r from-blue-500 to-purple-600" />
            <div className="p-5">
              <h2 className="text-xl font-bold mb-2">{a.headline}</h2>
              <p className="text-sm text-gray-400 mb-3">
                {a.source} ‚Ä¢ {a.date || "N/A"} {a.author ? `‚Ä¢ ${a.author}` : ""}
              </p>
              {a.link && (
                <a
                  href={a.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-400 hover:underline font-medium"
                >
                  Read more ‚Üí
                </a>
              )}
            </div>
          </div>
        ))}
      </div>

      {!loading && !error && articles.length === 0 && (
        <p className="text-center mt-10 text-gray-400">
          No results yet. Try a homepage like https://news.ycombinator.com or https://www.bbc.com/news
        </p>
      )}
    </div>
  );
}
