import { useState } from "react";

export default function App() {
  const [url, setUrl] = useState("");
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleScrape = async () => {
    if (!url) return;
    setLoading(true);
    setError("");
    setArticles([]);

    try {
      const response = await fetch(
        `http://localhost:5000/scrape?url=${encodeURIComponent(url)}`
      );
      const data = await response.json();

      if (response.ok) {
        setArticles(data);
      } else {
        setError(data.error || "Failed to fetch articles");
      }
    } catch (err) {
      setError("Server error: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Header */}
      <header className="p-6 text-center bg-gradient-to-r from-blue-600 to-purple-600 shadow-lg">
        <h1 className="text-4xl font-extrabold tracking-wide">üì∞ News Scraper</h1>
        <p className="text-gray-200 mt-2">Enter a site URL and scrape the latest articles</p>
      </header>

      {/* Input Section */}
      <div className="flex justify-center mt-8">
        <div className="bg-white/10 backdrop-blur-lg p-6 rounded-2xl shadow-xl w-full max-w-2xl">
          <div className="flex gap-3">
            <input
              type="text"
              placeholder="https://example.com"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              className="flex-grow p-3 rounded-lg text-black focus:ring-2 focus:ring-blue-500"
            />
            <button
              onClick={handleScrape}
              className="bg-blue-600 hover:bg-blue-700 px-6 py-3 rounded-lg font-semibold transition"
            >
              Scrape
            </button>
          </div>
        </div>
      </div>

      {/* Loading */}
      {loading && <p className="text-center mt-6">‚è≥ Scraping articles...</p>}

      {/* Error */}
      {error && <p className="text-center text-red-400 mt-6">{error}</p>}

      {/* Articles Grid */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8 p-8">
        {articles.map((article, i) => (
          <div
            key={i}
            className="bg-gray-800/80 rounded-2xl shadow-lg overflow-hidden hover:scale-105 transform transition duration-300"
          >
            {/* Image placeholder */}
            <div className="h-40 bg-gradient-to-r from-blue-500 to-purple-600"></div>

            {/* Content */}
            <div className="p-5">
              <h2 className="text-xl font-bold mb-2">{article.headline}</h2>
              <p className="text-sm text-gray-400 mb-3">
                {article.source} ‚Ä¢ {article.date || "N/A"}
              </p>
              <a
                href={article.link}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-400 hover:underline font-medium"
              >
                Read more ‚Üí
              </a>
            </div>
          </div>
        ))}
      </div>

      {/* Empty State */}
      {!loading && !error && articles.length === 0 && (
        <p className="text-center mt-16 text-gray-400">
          No articles yet. Try scraping a site!
        </p>
      )}
    </div>
  );
}
