import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [articles, setArticles] = useState([]);
  const [url, setUrl] = useState("https://news.ycombinator.com/");
  const [darkMode, setDarkMode] = useState(false);

  const scrapeSite = async () => {
    try {
      const res = await fetch(`http://localhost:4000/scrape?url=${url}`);
      const data = await res.json();
      setArticles(data.articles || []);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    scrapeSite();
  }, []);

  return (
    <div className={`App ${darkMode ? "dark" : "light"}`}>
      <h1 className="app-title">ðŸ“° News Scraper</h1>

      <button className="toggle-button" onClick={() => setDarkMode(!darkMode)}>
        {darkMode ? "Switch to Light Mode" : "Switch to Dark Mode"}
      </button>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Enter news site URL..."
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <button onClick={scrapeSite}>Scrape</button>
      </div>

      <div className="articles-container">
        {articles.length > 0 ? (
          articles.map((article, idx) => (
            <div className="article-card" key={idx}>
              <h2>{article.title}</h2>
              {article.snippet && <p>{article.snippet}</p>}
              {article.author && <p><strong>Author:</strong> {article.author}</p>}
              {article.date && <p><strong>Date:</strong> {article.date}</p>}
              <p><strong>Source:</strong> {article.source}</p>
              <a href={article.link} target="_blank" rel="noreferrer">
                Read More â†’
              </a>
            </div>
          ))
        ) : (
          <p className="no-articles">No articles yet. Try scraping!</p>
        )}
      </div>
    </div>
  );
}

export default App;
