import { useState } from "react";

export default function App() {
  const [url, setUrl] = useState("");
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleScrape = async () => {
    if (!url) return;
    setLoading(true);
    setError("");
    setArticles([]);

    try {
      const response = await fetch(`http://localhost:5000/scrape?url=${encodeURIComponent(url)}`);
      const data = await response.json();

      if (response.ok) {
        setArticles(data);
      } else {
        setError(data.error || "Failed to fetch articles");
      }
    } catch (err) {
      setError("Server error: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-6">üì∞ News Scraper</h1>

      {/* Input Section */}
      <div className="flex justify-center mb-6 gap-2">
        <input
          type="text"
          placeholder="Enter website URL..."
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          className="w-1/2 p-3 rounded-lg text-black"
        />
        <button
          onClick={handleScrape}
          className="bg-blue-500 hover:bg-blue-600 px-4 py-2 rounded-lg font-semibold"
        >
          Scrape
        </button>
      </div>

      {/* Loading */}
      {loading && <p className="text-center">‚è≥ Scraping articles...</p>}

      {/* Error */}
      {error && <p className="text-center text-red-400">{error}</p>}

      {/* Articles Grid */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {articles.map((article, i) => (
          <div
            key={i}
            className="bg-gray-800 p-4 rounded-xl shadow-lg hover:shadow-2xl transition"
          >
            <h2 className="text-xl font-semibold mb-2">{article.headline}</h2>
            <p className="text-sm text-gray-400 mb-2">
              {article.source} ‚Ä¢ {article.date || "N/A"}
            </p>
            <a
              href={article.link}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-400 hover:underline"
            >
              Read more ‚Üí
            </a>
          </div>
        ))}
      </div>

      {/* Empty State */}
      {!loading && !error && articles.length === 0 && (
        <p className="text-center mt-10 text-gray-400">No articles yet. Try scraping a site!</p>
      )}
    </div>
  );
}
