import React, { useEffect, useState } from "react";

function App() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [keyword, setKeyword] = useState("");
  const [sort, setSort] = useState("date");

  useEffect(() => {
    fetchNews();
  }, []);

  const fetchNews = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5000/scrape"); // backend route
      const data = await res.json();
      setNews(data);
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  const filteredNews = news
    .filter((item) =>
      item.title.toLowerCase().includes(keyword.toLowerCase())
    )
    .sort((a, b) => {
      if (sort === "date") {
        return new Date(b.date) - new Date(a.date);
      }
      return a.title.localeCompare(b.title);
    });

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-6">ðŸ“° News Scraper</h1>

      {/* Filters */}
      <div className="flex flex-col md:flex-row gap-4 justify-center mb-6">
        <input
          type="text"
          placeholder="Search keyword..."
          className="px-4 py-2 rounded-lg text-black w-full md:w-1/3"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />

        <select
          className="px-4 py-2 rounded-lg text-black"
          value={sort}
          onChange={(e) => setSort(e.target.value)}
        >
          <option value="date">Sort by Date</option>
          <option value="title">Sort by Title</option>
        </select>
      </div>

      {/* Loading Spinner */}
      {loading && (
        <div className="flex justify-center items-center">
          <div className="w-12 h-12 border-4 border-blue-500 border-dashed rounded-full animate-spin"></div>
        </div>
      )}

      {/* News Grid */}
      <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {filteredNews.map((item, index) => (
          <div
            key={index}
            className="bg-gray-800 p-5 rounded-2xl shadow-md hover:shadow-xl transition"
          >
            <h2 className="text-xl font-semibold mb-2">{item.title}</h2>
            {item.author && (
              <p className="text-sm text-gray-400">By {item.author}</p>
            )}
            {item.date && (
              <p className="text-sm text-gray-400">ðŸ“… {item.date}</p>
            )}
            <p className="text-sm text-gray-400 mt-1">
              Source: {item.source || "Unknown"}
            </p>
            <a
              href={item.link}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-block mt-4 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
            >
              Read More â†’
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
