// App.js
import React, { useState } from "react";
import "./App.css";

function App() {
  const [url, setUrl] = useState("");
  const [articles, setArticles] = useState([]);
  const [error, setError] = useState("");

  const handleScrape = async () => {
    setError("");
    try {
      const res = await fetch(`http://localhost:4000/scrape?url=${encodeURIComponent(url)}`);
      const data = await res.json();

      console.log("Scraped data:", data); // debug

      if (data.articles && Array.isArray(data.articles)) {
        setArticles(data.articles);
      } else {
        setArticles([]);
        setError("No articles found.");
      }
    } catch (err) {
      console.error("Error fetching:", err);
      setError("Something went wrong.");
    }
  };

  return (
    <div className="App">
      <h1>News Scraper</h1>

      <div className="input-bar">
        <input
          type="text"
          placeholder="Enter website URL (e.g. https://news.abs-cbn.com)"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <button onClick={handleScrape}>Scrape</button>
      </div>

      {error && <p className="error">{error}</p>}

      <div className="articles">
        {articles.length > 0 ? (
          articles.map((a, i) => (
            <div className="article-card" key={i}>
              <a href={a.link} target="_blank" rel="noopener noreferrer">
                {a.title}
              </a>
            </div>
          ))
        ) : (
          <p>No articles yet. Try scraping!</p>
        )}
      </div>
    </div>
  );
}

export default App;
