import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");

  // Fetch news from backend
  const fetchNews = () => {
    setLoading(true);
    fetch("http://localhost:5000/scrape") // adjust if backend runs elsewhere
      .then((res) => res.json())
      .then((data) => {
        setArticles(data.articles || []);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching:", err);
        setLoading(false);
      });
  };

  // Run once on mount
  useEffect(() => {
    fetchNews();
  }, []);

  // Filtered articles
  const filteredArticles = articles.filter((a) =>
    a.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="app-container">
      <h1 className="header">ðŸ“° News Scraper</h1>

      {/* Search + Scrape controls */}
      <div className="controls">
        <input
          type="text"
          placeholder="Search articles..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button onClick={fetchNews} disabled={loading}>
          {loading ? "Scraping..." : "Scrape News"}
        </button>
      </div>

      {/* Articles section */}
      {loading && <p>Loading articles...</p>}
      {!loading && filteredArticles.length === 0 && <p>No articles found.</p>}

      <div className="news-grid">
        {filteredArticles.map((article, index) => (
          <div key={index} className="news-card">
            <h2>{article.title}</h2>
            <p className="author">{article.author || "Unknown author"}</p>
            <a href={article.link} target="_blank" rel="noopener noreferrer">
              Read more â†’
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
