import React, { useState } from "react";
import "./App.css";

function App() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");
  const [siteUrl, setSiteUrl] = useState(""); // âœ… input for site

  const fetchNews = () => {
    if (!siteUrl) {
      alert("Please enter a site URL");
      return;
    }

    setLoading(true);
    fetch(`http://localhost:5000/scrape?url=${encodeURIComponent(siteUrl)}`)
      .then((res) => res.json())
      .then((data) => {
        setArticles(data || []); // âœ… backend returns array, not {articles}
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching:", err);
        setLoading(false);
      });
  };

  // âœ… filter by headline instead of title
  const filteredArticles = articles.filter((a) =>
    a.headline?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="app-container">
      <h1 className="header">ðŸ“° News Scraper</h1>

      {/* URL Input + Scrape button */}
      <div className="controls">
        <input
          type="text"
          placeholder="Enter news site URL..."
          value={siteUrl}
          onChange={(e) => setSiteUrl(e.target.value)}
        />
        <button onClick={fetchNews} disabled={loading}>
          {loading ? "Scraping..." : "Scrape"}
        </button>
      </div>

      {/* Search bar */}
      <div className="controls">
        <input
          type="text"
          placeholder="Search articles..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      {/* Articles section */}
      {loading && <p>Loading articles...</p>}
      {!loading && filteredArticles.length === 0 && <p>No articles found.</p>}

      <div className="news-grid">
        {filteredArticles.map((article, index) => (
          <div key={index} className="news-card">
            <h2>{article.headline}</h2>
            <p className="author">{article.author || "Unknown author"}</p>
            <a href={article.link} target="_blank" rel="noopener noreferrer">
              Read more â†’
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
