// App.js
import React, { useState } from "react";
import "./App.css";

function App() {
  const [url, setUrl] = useState("");
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleScrape = async (e) => {
    e.preventDefault();
    if (!url) return;

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:4000/scrape?url=${encodeURIComponent(url)}`);
      const data = await response.json();
      setArticles(data.articles || []);
    } catch (err) {
      console.error(err);
      setArticles([]);
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1>ðŸ“° News Scraper</h1>

      <form onSubmit={handleScrape}>
        <input
          type="text"
          placeholder="Enter a news site URL (e.g. https://news.ycombinator.com)"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <button type="submit">Scrape</button>
      </form>

      {loading ? (
        <p className="placeholder">Loading articles...</p>
      ) : articles.length === 0 ? (
        <p className="placeholder">No articles yet</p>
      ) : (
        <div className="articles">
          {articles.map((article, index) => (
            <div className="article-card" key={index}>
              <h3>
                <a href={article.link} target="_blank" rel="noopener noreferrer">
                  {article.title}
                </a>
              </h3>
              {article.snippet && <p className="snippet">{article.snippet}</p>}
              <div className="meta">
                {article.author && <span>By {article.author} â€¢ </span>}
                {article.date && <span>{article.date} â€¢ </span>}
                {article.source && <span>{article.source}</span>}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
