import React, { useState } from "react";
import axios from "axios";
import { FaSearch } from "react-icons/fa";

function App() {
  const [url, setUrl] = useState("");
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [keyword, setKeyword] = useState("");
  const [sortBy, setSortBy] = useState("date");

  const fetchArticles = async () => {
    if (!url) return;
    setLoading(true);
    try {
      const res = await axios.get("http://localhost:5000/scrape", {
        params: { url },
      });
      console.log("Fetched data:", res.data);

      // ✅ Only set if it's an array, otherwise fallback
      if (Array.isArray(res.data)) {
        setArticles(res.data);
      } else {
        setArticles([]);
      }
    } catch (err) {
      console.error(err);
      setArticles([]); // fallback on error
    }
    setLoading(false);
  };

  // ✅ Safe filter + sort
  const filtered = Array.isArray(articles)
    ? articles
        .filter((a) =>
          a.headline?.toLowerCase().includes(keyword.toLowerCase())
        )
        .sort((a, b) => {
          if (sortBy === "date") return new Date(b.date) - new Date(a.date);
          if (sortBy === "headline")
            return a.headline.localeCompare(b.headline);
          return 0;
        })
    : [];

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-6">📰 News Scraper</h1>
      {/* Input for URL */}
      <div className="flex justify-center mb-4 gap-2">
        <input
          type="text"
          placeholder="Enter website URL..."
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          className="w-1/2 px-4 py-2 rounded-lg text-black"
        />
        <button
          onClick={fetchArticles}
          className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-lg flex items-center"
        >
          <FaSearch className="mr-2" /> Scrape
        </button>
      </div>

      {/* Filter + Sort */}
      <div className="flex justify-center gap-4 mb-4">
        <input
          type="text"
          placeholder="Filter by keyword..."
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          className="px-3 py-2 rounded text-black"
        />
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="px-3 py-2 rounded text-black"
        >
          <option value="date">Sort by Date</option>
          <option value="headline">Sort by Headline</option>
        </select>
      </div>

      {/* Loading Spinner */}
      {loading && <p className="text-center">⏳ Loading...</p>}

      {/* Display Articles */}
      <div className="grid gap-4">
        {filtered.length > 0 ? (
          filtered.map((a, i) => (
            <div key={i} className="bg-gray-800 p-4 rounded-lg shadow">
              <h2 className="text-xl font-semibold">{a.headline}</h2>
              <p className="text-sm text-gray-400">
                {a.author} | {a.date} | {a.source}
              </p>
              <a
                href={a.link}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-400 underline"
              >
                Read more
              </a>
            </div>
          ))
        ) : (
          !loading && <p className="text-center">No articles found.</p>
        )}
      </div>
    </div>
  );
}

export default App;
